function filterGifts(gifts: string[]): string[] {
  let cleanGifts: string[] = []

  for (let i = 0; i < gifts.length; i++) {
    const gift = gifts[i]
    if (!gift.includes("#")) {
      cleanGifts.push(gift)
    }
  }

  return cleanGifts
}

function manufactureGifts(
  giftsToProduce: Array<{ toy: string, quantity: number }>
): string[] {
  let giftsForProduce: string[] = []
  for (let i = 0; i < giftsToProduce.length; i++) {
    const currentGift = giftsToProduce[i]
    if(!Number(currentGift.quantity)) continue
    if (currentGift.quantity < 0) continue
    for (let j = 1; j <= currentGift.quantity; j++) {
      giftsForProduce.push(currentGift.toy)
    }
  }
  return giftsForProduce
}

function drawGift(size: number, symbol: string): string {
  if (size < 2) return ""
  let giftDraw: string = ""

  const firstLine = symbol.repeat(size)
  const lastLine = firstLine

  giftDraw += firstLine
  giftDraw += "\n"
  for (let i = 0; i < size - 2; i++) {
    const spaceForMiddleLine = " "
    const middleLine = symbol + spaceForMiddleLine.repeat(size - 2) + symbol
    giftDraw += middleLine
    giftDraw += "\n"
  }
  giftDraw += lastLine
  return giftDraw
}
